  --telegram-divider: hsl(220 27% 18%);
  --telegram-text: hsl(0 0% 95%);
  --telegram-text-secondary: hsl(220 8% 65%);
  --telegram-accent: hsl(213 94% 68%);
  --telegram-success: hsl(142 76% 36%);
  --telegram-warning: hsl(38 92% 50%);
  --telegram-error: hsl(0 84% 60%);
  --telegram-pending: hsl(43 96% 56%);
  --telegram-bg-dark: hsl(220 27% 10%);
  --telegram-bg-secondary: hsl(220 27% 12%);
  --telegram-border-dark: hsl(220 27% 18%);
  --telegram-text-secondary-dark: hsl(220 8% 65%);
  --telegram-dark: hsl(0 0% 95%);
}

@layer base {
  * {
    @apply border-border;
  }

  html, #root {
    direction: rtl;
    height: 100vh;
    height: -webkit-fill-available;
    margin: 0;
    padding: 0;
    overflow: hidden;
    box-sizing: border-box;
  }

  body {
    @apply antialiased;
    font-family: var(--font-telegram);
    background-color: var(--telegram-bg);
    color: var(--telegram-text);
    line-height: 1.6;
    height: 100vh;
    height: -webkit-fill-available;
    margin: 0;
    padding: 0;
    overflow-x: hidden;
    overflow-y: hidden;
    box-sizing: border-box;
    width: 100%;
  }

  .app-container {
    height: 100vh;
    height: -webkit-fill-available;
    display: flex;
    flex-direction: column;
    width: 100%;
    overflow: hidden;
    position: relative;
    box-sizing: border-box;
    margin: 0;
    padding: 0;
  }

  .main-content {
    flex: 1;
    width: 100%;
    height: calc(100vh - 64px);
    overflow-y: auto;
    overflow-x: hidden;
    padding: 0;
    margin: 0;
    box-sizing: border-box;
  }

  header {
    height: 64px;
    flex-shrink: 0;
    box-sizing: border-box;
  }

  .bottom-navigation {
    display: none;
  }

  * {
    box-sizing: border-box;
  }

  /* Page Transition Animations */
  @keyframes slideInRight {
    from {
      opacity: 0;
      transform: translateX(30px);
    }
    to {
      opacity: 1;
      transform: translateX(0);
    }
  }

  @keyframes slideInLeft {
    from {
      opacity: 0;
      transform: translateX(-30px);
    }
    to {
      opacity: 1;
      transform: translateX(0);
    }
  }

  @keyframes slideInUp {
    from {
      opacity: 0;
      transform: translateY(20px);
    }
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }

  @keyframes fadeIn {
    from {
      opacity: 0;
    }
    to {
      opacity: 1;
    }
  }

  @keyframes scaleIn {
    from {
      opacity: 0;
      transform: scale(0.95);
    }
    to {
      opacity: 1;
      transform: scale(1);
    }
  }

  .page-transition {
    animation: slideInRight 0.3s ease-out;
  }

  .page-transition-up {
    animation: slideInUp 0.4s ease-out;
  }

  .page-transition-fade {
    animation: fadeIn 0.25s ease-out;
  }

  .page-transition-scale {
    animation: scaleIn 0.3s ease-out;
  }

  /* Tab content transitions */
  .tab-content-enter {
    animation: slideInUp 0.3s ease-out;
  }

  /* Card hover transitions */
  .card-hover-transition {
    transition: all 0.2s ease-in-out;
  }

  .card-hover-transition:hover {
    transform: translateY(-2px);
    box-shadow: 0 8px 25px rgba(0, 0, 0, 0.15);
  }

  /* Button press animation */
  .btn-press {
    transition: transform 0.1s ease-in-out;
  }

  .btn-press:active {
    transform: scale(0.98);
  }

  /* Enhanced floating support button animation */
  @keyframes slideUp {
    from {
      opacity: 0;
      transform: translateY(20px) scale(0.9);
    }
    to {
      opacity: 1;
      transform: translateY(0) scale(1);
    }
  }

  .animate-slideUp {
    animation: slideUp 0.6s cubic-bezier(0.4, 0, 0.2, 1) forwards;
    animation-delay: 1s;
    opacity: 0;
  }

  /* Responsive tab scrolling */
  .tab-scroll {
    overflow-x: auto;
    scrollbar-width: none;
    -ms-overflow-style: none;
  }

  .tab-scroll::-webkit-scrollbar {
    display: none;
  }

  /* Tab responsive improvements */
  .tabs-list-responsive {
    display: grid;
    grid-template-columns: repeat(4, 1fr);
    width: 100%;
    min-width: 100%;
    gap: 0;
  }

  .tabs-list-responsive [data-state="active"] {
    background-color: var(--telegram-blue);
    color: white;
  }

  .tabs-trigger-responsive {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    gap: 4px;
    padding: 8px 4px;
    min-height: 60px;
    font-size: 10px;
    white-space: nowrap;
    text-overflow: ellipsis;
    overflow: hidden;
  }

  @media (min-width: 480px) {
    .tabs-trigger-responsive {
      flex-direction: row;
      gap: 6px;
      font-size: 12px;
      padding: 10px 8px;
    }
  }

  @media (min-width: 768px) {
    .tabs-trigger-responsive {
      font-size: 14px;
      padding: 12px 16px;
    }
  }

  /* Filter section responsive */
  .filter-tabs-responsive {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    gap: 4px;
    width: 100%;
  }

  @media (min-width: 640px) {
    .filter-tabs-responsive {
      grid-template-columns: repeat(4, 1fr);
    }
  }

  /* Mobile-first responsive grid */
  .responsive-grid {
    display: grid;
    gap: 1rem;
    grid-template-columns: 1fr;
  }

  @media (min-width: 640px) {
    .responsive-grid {
      grid-template-columns: repeat(2, 1fr);
    }
  }

  @media (min-width: 1024px) {
    .responsive-grid {
      grid-template-columns: repeat(3, 1fr);
    }
  }
}

@layer utilities {
  .font-telegram {
    font-family: var(--font-telegram);
  }

  /* Color utilities */
  .bg-telegram-blue {
    background-color: var(--telegram-blue);
  }

  .bg-telegram-blue-light {
    background-color: var(--telegram-blue-light);
  }

  .bg-telegram-bg {
    background-color: var(--telegram-bg);
  }

  .bg-telegram-surface {
    background-color: var(--telegram-surface);
  }

  .bg-telegram-surface-variant {
    background-color: var(--telegram-surface-variant);
  }

  .bg-telegram-section {
    background-color: var(--telegram-section-bg);
  }

  .bg-telegram-success {
    background-color: var(--telegram-success);
  }

  .bg-telegram-warning {
    background-color: var(--telegram-warning);
  }

  .bg-telegram-error {
    background-color: var(--telegram-error);
  }

  .bg-telegram-pending {
    background-color: var(--telegram-pending);
  }

  .text-telegram-blue {
    color: var(--telegram-blue);
  }

  .text-telegram {
    color: var(--telegram-text);
  }

  .text-telegram-secondary {
    color: var(--telegram-text-secondary);
  }

  .text-telegram-success {
    color: var(--telegram-success);
  }

  .text-telegram-warning {
    color: var(--telegram-warning);
  }

  .text-telegram-error {
    color: var(--telegram-error);
  }

  .border-telegram {
    border-color: var(--telegram-divider);
  }

  .border-telegram-blue {
    border-color: var(--telegram-blue);
  }

  /* Enhanced shadows */
  .shadow-telegram {
    box-shadow: var(--shadow-telegram);
  }

  .shadow-telegram-lg {
    box-shadow: var(--shadow-telegram-lg);
  }

  .shadow-telegram-xl {
    box-shadow: var(--shadow-telegram-xl);
  }

  /* Modern border radius */
  .rounded-telegram {
    border-radius: var(--radius);
  }

  .rounded-telegram-sm {
    border-radius: var(--border-radius-sm);
  }

  .rounded-telegram-lg {
    border-radius: var(--border-radius-lg);
  }

  /* Responsive utilities */
  .safe-area-bottom {
    padding-bottom: calc(1rem + var(--safe-area-inset-bottom));
  }

  .safe-area-top {
    padding-top: var(--safe-area-inset-top);
  }

  /* Modern table styles */
  .table-modern {
    @apply border-collapse border-spacing-0 w-full;
  }

  .table-modern th {
    @apply bg-telegram-surface-variant border-b border-telegram px-4 py-3 text-right font-medium;
    color: var(--telegram-text);
  }

  .table-modern td {
    @apply border-b border-telegram px-4 py-3;
    color: var(--telegram-text);
  }

  .table-modern tbody tr:hover {
    @apply bg-telegram-surface-variant;
    opacity: 0.7;
  }

  .table-modern tbody tr:nth-child(even) {
    @apply bg-telegram-surface-variant;
    opacity: 0.3;
  }

  /* Modern form styles */
  .form-modern .form-field {
    @apply space-y-2;
  }

  .form-modern .form-label {
    @apply text-sm font-medium text-telegram;
  }

  .form-modern .form-input {
    @apply w-full px-3 py-2 border bg-telegram-surface text-telegram focus:outline-none focus:border-transparent;
    border-color: var(--telegram-divider);
    border-radius: var(--radius);
  }

  .form-modern .form-input:focus {
    border-color: var(--telegram-blue);
    box-shadow: 0 0 0 2px rgba(33, 150, 243, 0.2);
  }

  .form-modern .form-input::placeholder {
    color: var(--telegram-text-secondary);
  }

  /* Animation utilities */
  .animate-fade-in {
    animation: fadeIn 0.3s ease-in-out;
  }

  .animate-slide-up {
    animation: slideUp 0.3s ease-out;
  }

  @keyframes fadeIn {
    from { opacity: 0; }
    to { opacity: 1; }
  }

  @keyframes slideUp {
    from { 
      opacity: 0;
      transform: translateY(20px);
    }
    to { 
      opacity: 1;
      transform: translateY(0);
    }
  }

  /* Status badge styles */
  .status-badge-pending {
    @apply bg-telegram-pending text-white px-2 py-1 rounded-sm text-xs font-medium;
  }

  .status-badge-approved {
    @apply bg-telegram-success text-white px-2 py-1 rounded-sm text-xs font-medium;
  }

  .status-badge-rejected {
    @apply bg-telegram-error text-white px-2 py-1 rounded-sm text-xs font-medium;
  }

  /* Enhanced Raffle Tab Styling */
  .raffle-tab {
    @apply transition-all duration-300 font-medium text-sm;
    border-radius: 12px;
    min-height: 44px;
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 6px;
    padding: 8px 16px;
    margin: 2px;
    position: relative;
    overflow: hidden;
    border: none !important;
  }

  .raffle-tab::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: currentColor;
    opacity: 0;
    transition: opacity 0.3s ease;
    border-radius: inherit;
  }

  .raffle-tab:hover::before {
    opacity: 0.08;
  }

  .raffle-tab-pending {
    @apply text-blue-700 bg-blue-50;
    color: #1e40af;
    display: flex;
    align-items: center;
    gap: 4px;
  }

  .raffle-tab-pending[data-state="active"] {
    @apply bg-blue-500 text-white shadow-lg;
    box-shadow: 0 4px 12px rgba(59, 130, 246, 0.3);
  }

  .raffle-tab-rejected {
    @apply text-red-700 bg-red-50;
    color: #dc2626;
    display: flex;
    align-items: center;
    gap: 4px;
  }

  .raffle-tab-rejected[data-state="active"] {
    @apply bg-red-500 text-white shadow-lg;
    box-shadow: 0 4px 12px rgba(239, 68, 68, 0.3);
  }

  .raffle-tab-approved {
    @apply text-green-700 bg-green-50;
    color: #16a34a;
    display: flex;
    align-items: center;
    gap: 4px;
  }

  .raffle-tab-approved[data-state="active"] {
    @apply bg-green-500 text-white shadow-lg;
    box-shadow: 0 4px 12px rgba(34, 197, 94, 0.3);
  }
}

/* Modern Telegram-like animations */
@keyframes telegram-bounce {
  0% { transform: scale(0.96); opacity: 0.8; }
  50% { transform: scale(1.02); }
  100% { transform: scale(1); opacity: 1; }
}

@keyframes telegram-slide-up {
  from { transform: translateY(16px); opacity: 0; }
  to { transform: translateY(0); opacity: 1; }
}

@keyframes telegram-slide-in {
  from { transform: translateX(-20px); opacity: 0; }
  to { transform: translateX(0); opacity: 1; }
}

@keyframes telegram-pulse {
  0%, 100% { opacity: 1; }
  50% { opacity: 0.7; }
}

@keyframes telegram-gradient {
  0% { background-position: 0% 50%; }
  50% { background-position: 100% 50%; }
  100% { background-position: 0% 50%; }
}

.telegram-bounce {
  animation: telegram-bounce 0.4s cubic-bezier(0.34, 1.56, 0.64, 1);
}

.telegram-slide-up {
  animation: telegram-slide-up 0.5s cubic-bezier(0.25, 0.46, 0.45, 0.94);
}

.telegram-slide-in {
  animation: telegram-slide-in 0.4s cubic-bezier(0.25, 0.46, 0.45, 0.94);
}

.message-bubble {
  position: relative;
  background: linear-gradient(135deg, var(--telegram-blue) 0%, var(--telegram-blue-light) 100%);
  border-radius: 18px 18px 4px 18px;
  color: white;
  padding: 12px 16px;
  margin: 8px 0;
  box-shadow: var(--shadow-telegram);
  transition: all 0.2s ease;
}

.message-bubble:hover {
  transform: translateY(-1px);
  box-shadow: var(--shadow-telegram-lg);
}

.message-bubble::before {
  content: '';
  position: absolute;
  bottom: -4px;
  right: 12px;
  width: 0;
  height: 0;
  border: 8px solid transparent;
  border-top-color: var(--telegram-blue-light);
}

.telegram-card {
  background: var(--telegram-surface);
  border-radius: var(--radius);
  border: 1px solid var(--telegram-divider);
  transition: all 0.2s cubic-bezier(0.4, 0, 0.2, 1);
  overflow: hidden;
}

.telegram-card:hover {
  transform: translateY(-2px);
  border-color: var(--telegram-blue);
  box-shadow: var(--shadow-telegram-lg);
}

.telegram-card-elevated {
  background: var(--telegram-surface);
  border-radius: var(--radius);
  box-shadow: var(--shadow-telegram-lg);
  border: none;
}

.star-icon {
  color: #FFD700;
  filter: drop-shadow(0 2px 4px rgba(255, 215, 0, 0.3));
}

.premium-badge {
  background: linear-gradient(135deg, #FF6B35 0%, #F7931E 100%);
  color: white;
  border-radius: 1rem;
  padding: 0.25rem 0.75rem;
  font-size: 0.75rem;
  font-weight: 600;
}

/* Modern Floating Action Button */
.floating-button {
  position: fixed;
  bottom: 88px;
  left: 20px;
  width: 56px;
  height: 56px;
  background: var(--telegram-blue);
  border-radius: 16px;
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  font-size: 24px;
  box-shadow: 0 4px 16px rgba(0, 0, 0, 0.15);
  transition: all 0.2s cubic-bezier(0.4, 0, 0.2, 1);
  cursor: pointer;
  z-index: 50;
  border: none;
}

.floating-button:hover {
  transform: scale(1.05);
  background: var(--telegram-blue-dark);
  box-shadow: 0 6px 20px rgba(0, 0, 0, 0.2);
}

.floating-button:active {
  transform: scale(0.95);
}

/* RTL-specific adjustments */
[dir="rtl"] .message-bubble {
  border-radius: 0.75rem 0.75rem 0.75rem 0.25rem;
}

[dir="rtl"] .message-bubble {
  border-radius: 18px 18px 18px 4px;
}

[dir="rtl"] .message-bubble::before {
  right: auto;
  left: 12px;
  border-top-color: var(--telegram-blue-light);
}

[dir="rtl"] .floating-button {
  left: 20px;
  right: auto;
}

/* Responsive design for mobile-first Telegram mini-app */
@media (max-width: 768px) {
  .telegram-card {
    margin: 0.5rem;
  }

  .floating-button {
    bottom: 5rem; /* Account for bottom navigation */
  }
}

