import { useState, useEffect } from "react";
import { HelpCircle, MessageCircle } from "lucide-react";
import { useQuery } from "@tanstack/react-query";

interface FloatingSupportButtonProps {
  userRole?: string;
}

export function FloatingSupportButton({ userRole = "regular" }: FloatingSupportButtonProps) {
  const [isVisible, setIsVisible] = useState(false);

  // Only show for regular users
  if (userRole !== "regular") {
    return null;
  }

  // Fetch bot config to get support link
  const { data: botConfig } = useQuery({
    queryKey: ["/api/bot/config"],
    refetchInterval: 60000, // Refresh every minute
  });

  useEffect(() => {
    const timer = setTimeout(() => {
      setIsVisible(true);
    }, 1000);

    return () => clearTimeout(timer);
  }, []);

  const handleSupportClick = () => {
    if (botConfig?.supportLink) {
      // Open support link in new tab
      window.open(botConfig.supportLink, '_blank', 'noopener,noreferrer');
    }
  };

  // Don't show if no support link is configured
  if (!botConfig?.supportLink) {
    return null;
  }

  return (
    <div className={`floating-support-button ${isVisible ? 'visible' : ''}`}>
      <button
        onClick={handleSupportClick}
        className="support-button btn-press"
        title="Ù¾Ø´ØªÛŒØ¨Ø§Ù†ÛŒ"
        aria-label="Ø¯Ú©Ù…Ù‡ Ù¾Ø´ØªÛŒØ¨Ø§Ù†ÛŒ"
      >
        <MessageCircle size={24} />
        <span className="support-text">Ù¾Ø´ØªÛŒØ¨Ø§Ù†ÛŒ</span>
      </button>
    </div>
  );
}

